<?phpnamespace Estrutura\Validator;use Zend\Validator\AbstractValidator;use Zend\Validator\Exception;class Cid extends AbstractValidator {    const INVALID = 'invalid';    const WRONG_LENGTH = 'wrongLength';    /**     * Validation failure message template definitions     *     * @var array     */    protected $messageTemplates = array(        self::INVALID => "O valor de entrada não corresponde ao valor de um CID válido",        self::WRONG_LENGTH => "O valor de entrada deve conter 36 digitos",    );    /**     * Sets validator options     *     * @param  array|Traversable $options     * @throws Exception\InvalidArgumentException     */    public function __construct($options = null) {        parent::__construct($options);    }    /**     * Returns true if and only if $value is greater than min option     *     * @param  mixed $cpf     * @return bool     */    public function isValid($cid) {        if (strlen($cid) != 36) {            $this->error(self::WRONG_LENGTH);            return false;        } elseif (!preg_match('/^\w{8}-\w{4}-\w{4}-\w{4}-\w{12}$/', $cid)) {            return false;        }        return true;    }}